import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image } from 'react-native';


function Screen_login() {
  return (
    <View style={styles.container}>
      <View style={styles.logo_area}>
        <View style={styles.logo_banner}>
          <Image style={styles.logo_style} source={require('../image/logo.png')} />
          <View>
            <Text style={styles.logo_banner_text_1}>SSKĐT</Text>
            <Text style={styles.logo_banner_text_2}>Sổ sức khỏe điện tử</Text>
          </View>
        </View>
      </View>
      <View style={styles.login_area}>
        <Text style = {styles.login_label}>Đăng nhập</Text>
      </View> 

    </View>
  );
}
export default Screen_login;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundImage: 'linear-gradient(to right, #3390fb, #55bbfb)',
    alignItems: 'center',
    justifyContent: 'center',

  },
  logo_area: {
    width: '100%',
    height: '30%',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo_banner: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  logo_style: {
    width: '100px',
    height: '100px'
  },
  logo_banner_text_1: {
    color: 'white',
    fontSize: '45px',
    fontWeight: '500',
  },
  logo_banner_text_2: {
    color: 'white',
    fontSize: '15px',
    fontWeight: '350',
  },
  login_area: {

    backgroundColor: '#f1f9ff',
    width: '100%',
    height: '80%',
    marginBottom: '-50px',
    borderRadius: '35px',
    alignItems: 'center',
    justifyContent: 'center',
  },
  login_label:{
    fontWeight: 'bold',
    fontSize: '15px'

  }

});
